func:
// Check if the response contains data
if (msg.payload && msg.payload.length > 0) {
    // Extract quote, author, and category from the response
    let quote = msg.payload[0].quote;
    let author = msg.payload[0].author;
    let category = msg.payload[0].category;  // Assuming category was passed in msg before the API call

    // Format the output for display
    msg.payload = `"${quote}","Author:${author}","category:${category}"`;
} else {
    // Handle case where no quote is found
    msg.payload = "No quote found for the selected category.";
}

return msg;
